class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({method:"GET",url:$(e).attr("href")}).done((function(t){let l=parseInt($(e).attr("data-likes"));t.data.deleted?l-=1:l+=1,$(e).attr("data-likes",l),$(`#like-count-${t.data.likable}-${t.data.type}`).html(""+l)})).fail((function(t){console.log("error in generating like !",t)}))}))}}$(".like-btn-link").each((function(){new ToggleLike(this)}));